import { useState } from "react";

const AdoptForm = () => {

    const [formData, setFormData] = useState({
        name: '',
        nameKaren: '',
        email: '',
        phone: '',
        message: '',
    });

    const [errors, setErrors] = useState({
        name: '',
        nameKaren: '',
        email: '',
        phone: '',
        message: '',
    });

    const handleChange = (event) => {
        setFormData({
            ...formData,
            [event.target.nameKaren]: event.target.value,
        });

        setErrors({
            ...errors,
            [event.target.nameKaren]: '',
        });
    };

    const validate = () => {
        let nameError = '';
        let nameKarenError = ''
        let emailError = '';
        let phoneError = '';
        let messageError = '';

        if (!formData.name) {
            nameError = 'Name is required';
        }

        if (!formData.email) {
            emailError = 'Email is required';
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
            emailError = 'Email is not valid';
        }

        if (!formData.message) {
            messageError = 'Message is required';
        }

        if (!formData.phone) {
            phoneError = 'Number phone is required';
        }

        if (nameError || emailError || messageError || phoneError) {
            setErrors({ name: nameError, email: emailError, message: messageError, phone: phoneError });
            return false;
        }

        return true;
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        const isValid = validate();
        if (isValid) {
            console.log(formData);
            setFormData({ name: '', email: '', message: '', phone: '' });
        }
    };





    return (
        <div>


        </div>
    )
}

export default AdoptForm
