import { useState } from "react";

const AdoptForm = () => {

    const [formData, setFormData] = useState({
        name: '',
        nameKaren: '',
        email: '',
        phone: '',
        message: '',
    });

    const [errors, setErrors] = useState({
        name: '',
        nameKaren: '',
        email: '',
        phone: '',
        message: '',
    });

    const handleChange = (event) => {
        setFormData({
            ...formData,
            [event.target.nameKaren]: event.target.value,
        });

        setErrors({
            ...errors,
            [event.target.nameKaren]: '',
        });
    };

    const validate = () => {
        let nameError = '';
        let nameKarenError = '';
        let emailError = '';
        let phoneError = '';
        let messageError = '';

        if (!formData.name) {
            nameError = 'Name is required';
        }

        if (!formData.nameKaren) {
            nameKarenError = 'Name is required';
        }

        if (!formData.email) {
            emailError = 'Email is required';
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
            emailError = 'Email is not valid';
        }

        if (!formData.message) {
            messageError = 'Message is required';
        }

        if (!formData.phone) {
            phoneError = 'Number phone is required';
        }

        if (nameError || nameKarenError ||emailError || messageError || phoneError) {
            setErrors({ name: nameError, nameKaren: nameKarenError, email: emailError, message: messageError, phone: phoneError });
            return false;
        }

        return true;
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        const isValid = validate();
        if (isValid) {
            console.log(formData);
            setFormData({ name: '', nameKaren: '', email: '', message: '', phone: '' });
        }
    };





    return (
        <div>
            <form onSubmit={handleSubmit}>
            <div>
                <label>Nombre</label>
                <br/>
                <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    id='username'
                />
                {errors.name && <div style={{ color: 'red' }}>{errors.name}</div>}
            </div>

            <div>
            <label>Email</label>
                <br/>
                <input
                    type="text"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    id='email'
                />
                {errors.email && <div style={{ color: 'red' }}>{errors.email}</div>}
            </div>

            <div>
            <label>Teléfono</label>
                <br/>
                <input
                    type="text"
                    name="phone"
                    placeholder="+1 (555) 987-6543"
                    value={formData.phone}
                    onChange={handleChange}
                    id='tel'
                />
                {errors.phone && <div style={{ color: 'red' }}>{errors.phone}</div>}
            </div>

            <div>
                <textarea
                    name="message"
                    placeholder="¿En qué podemos ayudarte?"
                    value={formData.message}
                    onChange={handleChange}
                />
                {errors.message && <div style={{ color: 'red' }}>{errors.message}</div>}
            </div>

            <button type='submit' className={'btn-form'} text={'Enviar'} />
        </form>


        </div>
    )
}

export default AdoptForm
