import React, { useContext, useEffect, useRef } from "react"
import { CatsApi } from "../../context/CatsApi"
import CardCat from "../CardCat/CardCat";

import './Carrousel.css'



const Carrousel = () => {

    const { cats } = useContext(CatsApi);

    const sliceCats = cats.slice(0, 7)
    const carrouselRef = useRef(null);

    useEffect(() => {
        const interval = setInterval(() => {
          if (carrouselRef.current) {
            carrouselRef.current.scrollLeft += 1;
          }
        }, 10);

        return () => clearInterval(interval);
      }, []);





    return (
        <div id="slider-container" ref={carrouselRef}>
                {sliceCats.map((cat) => {
                    return (
                        <CardCat
                            name={cat.name}
                            id={cat.id}
                            description={cat.description}
                            img={cat.img}
                        />
                    )
                })}
        </div>
    );
};

export default Carrousel;
