import { useContext } from "react"
import { CatsApi } from "../../context/CatsApi"


const Carrousel = () => {

    const { cats } = useContext(CatsApi)


    return (
        <div className='carousel-cats'>


            {cats.map((cat) => {
                return (
                    <div class>
                        <input checked type='radio' name="slider" id="slide1" />
                        <div className="slider-wrapper">
                            <div className='inner'>
                                <article>
                                    <div className="info top-left">
                                        <h3>Malacca</h3></div>
                                    <img src="https://farm9.staticflickr.com/8059/28286750501_dcc27b1332_h_d.jpg" />
                                </article>
                            </div>

                        </div>


                        <div className="slider-prev-next-control">
                            <label for='slide1'></label>
                        </div>


                        <div class="slider-dot-control">
                            <label for='slide1'></label>
                        </div>
                    </div>
                )

            })}







        </div>
    )
}

export default Carrousel
