import { useContext } from "react"
import { CatsApi } from "../../context/CatsApi"
import './Car'


const Carrousel = () => {

    const { cats } = useContext(CatsApi)


    return (
        <div className='carousel-cats'>


            {cats.map((cat) => {
                return (
                    <div className="wrapped">
                        <input checked type='radio' name="slider" id={cat.id} />
                        <div className="slider-wrapper">
                            <div className='inner'>
                                <article>
                                    <div className="info top-left">
                                        <h3>{cat.name}</h3></div>
                                    <img src={cat.img} alt={cat.name} />
                                </article>
                            </div>

                        </div>
                        <div className="slider-prev-next-control">
                            <label for={cat.id}></label>
                        </div>


                        <div class="slider-dot-control">
                            <label for={cat.id}></label>
                        </div>
                    </div>
                )

            })}







        </div>
    )
}

export default Carrousel
