import React, { useContext, useEffect, useState } from "react"
import { CatsApi } from "../../context/CatsApi"
import CardCat from "../CardCat/CardCat";
import './Carrousel.css'


const Carrousel = () => {

    const { cats } = useContext(CatsApi);
    const [activeIndex, setActiveIndex] = useState(0);

    const sliceCats = cats.slice(0, 7)

    useEffect(() => {
        const interval = setInterval(() => {
          setActiveIndex((activeIndex) =>
            activeIndex === sliceCats.length - 1 ? 0 : activeIndex + 1
          );
        }, 5000);
        return () => clearInterval(interval);
      }, [sliceCats.length]);

    return (
        <div id="slider-container">
            <div className="slider-wrapper" style={{ transform: `translateX(-${activeIndex * 100}%)` }}>
            {sliceCats.map((cat) => {
                return (
                    <CardCat
                        name={cat.name}
                        id={cat.id}
                        description={cat.description}
                        img={cat.img}
                    />
                )
            })}</div>

        </div>
    );
};

export default Carrousel;
