import { useContext, useState } from "react";
import { CatsApi } from "../../context/CatsApi";
import CardCat from "../CardCat/CardCat";


const InputCats = () => {

    const { cats } = useContext(CatsApi);
    const [searchName, setSearchName] = useState("");
    const [searchLocation, setSearchLocation] = useState("");

    const filteredCats = cats.filter((cat) => {
      const nameMatch = cat.name.toLowerCase().includes(searchName.toLowerCase());
      const locationMatch = cat.location.toLowerCase().includes(searchLocation.toLowerCase());
      return nameMatch && locationMatch;
    });

    const handleSearch = () => {
       // Aquí puedes hacer algo con los gatos filtrados, como guardarlos en el estado o mostrarlos en la página
    };


    return (
        <div>
            <h2>ENCUENTRA TU MICHI</h2>
            <h4>Seguro que hay uno muy cerca de ti</h4>

            <label>Nombre</label>
            <input type='text' value={searchName} onChange={(e) => setSearchName(e.target.value)} />
            <label>Ubicación</label>
            <input type='text' value={searchLocation} onChange={(e) => setSearchLocation(e.target.value)}/>

            <button onClick={handleSearch}>BUSCAR</button>

            <div style={{display: 'flex', flexWrap: 'wrap', justifyContent: 'space-evenly'}}>

                {filteredCats.map((cat, index) => {
                    return (
                        <CardCat
                            key={index}
                            name={cat.name}
                            id={cat.id}
                            location={cat.location}
                            description={cat.description}
                            img={cat.img}
                        />
                    )
                })}
            </div>

        </div>
    )
}

export default InputCats
