import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import { useEffect, useState } from 'react';
import PlacesAutocomplete, { geocodeByAddress, getLatLng } from 'react-places-autocomplete';
import './GoogleMaps.css'

const libraries = ['places'];

const defaultCenter = {
    lat: 40.4131401, lng: -3.706334
}

const GoogleMaps = () => {

    const [address, setAddress] = useState("");
    const [center, setCenter] = useState(defaultCenter);
    const [markerPosition, setMarkerPosition] = useState(null);
    const [loading, setLoading] = useState(true);

    const mapStyles = {
        height: "100vh",
        width: "100%",
        border: '15px solid #ffbadb'
    };

    const handleSelect = async (value) => {
        const results = await geocodeByAddress(value);
        const latLng = await getLatLng(results[0]);
        console.log(latLng);
        setAddress(value);
        setCenter(latLng);
        setMarkerPosition(latLng);
    };

    useEffect(() => {
        let timer = setTimeout(() => {
            setLoading(false);
        }, 5000);
        return () => clearTimeout(timer);
    }, []);

    const onLoad = () => {
        setLoading(false);
    }



    return (
        <div style={{ margin: '120px 0', display: 'block', alignItems: 'center', textAlign: 'center' }}>
            <h3 style={{ color: '#ffbadb', fontWeight: '600,', padding: '50px 0 30px 0', fontSize: '50px' }}>Encuentra casas de acogida o centros cercanos</h3>

            <LoadScript googleMapsApiKey='AIzaSyDiT0oMbPlcoyQWL8eCXMiE71TmL1QYdOE' libraries={libraries}>
                <PlacesAutocomplete
                    value={address}
                    onChange={setAddress}
                    onSelect={handleSelect}
                >
                    {({ getInputProps, suggestions, getSuggestionItemProps, loading }) => (
                        <div>
                            <input {...getInputProps({ placeholder: "Gatoteca..." })} style={{ border: '1px solid rgb(224, 225, 227)', width: '500px', borderRadius: '500px', backgroundColor: 'rgb(243, 243, 243)', padding: '6px 5px 6px 10px', margin: '10px 0 30px 0' }} />
                            <div className="autocomplete-items">
                                {loading ? <div>Cargando...</div> : null}

                                {suggestions.map((suggestion) => {
                                    const style = {
                                        backgroundColor: suggestion.active ? "#ffbadb" : "transparent",
                                        padding: '10px'
                                    };
                                    return (
                                        <div key={suggestion.placeId} {...getSuggestionItemProps(suggestion, { style })}>
                                            {suggestion.description}
                                        </div>
                                    );
                                })}
                            </div>
                        </div>
                    )}
                </PlacesAutocomplete>


                <GoogleMap
                    mapContainerStyle={mapStyles}
                    zoom={13}
                    center={center}
                >
                    {markerPosition !== null && <Marker position={markerPosition} />}
                </GoogleMap>


            </LoadScript>
        </div>
    )
}

export default GoogleMaps
