import { useContext, useState } from "react";
import { CatsApi } from "../../context/CatsApi";
import CardCat from "../CardCat/CardCat";


const InputCats = () => {

    const { cats } = useContext(CatsApi);

    const [inputName, setInputName] = useState("");
    const [inputLocation, setInputLocation] = useState("");

    const handleNameChange = (event) => {
        setInputName(event.target.value);
    };

    const handleLocationChange = (event) => {
        setInputLocation(event.target.value);
    };

    const filterCats = () => {
        let filteredCats = cats;

        if (inputName && inputName.length > 0) {
            filteredCats = filteredCats.filter((cat) =>
                cat.name.toLowerCase().startsWith(inputName.toLowerCase())
            );
        }

        if (inputLocation && inputLocation.length > 0) {
            filteredCats = filteredCats.filter((cat) =>
                cat.location.toLowerCase().startsWith(inputLocation.toLowerCase())
            );
        }

        return filteredCats;
    };

    return (
        <div>
            <h2>ENCUENTRA TU MICHI</h2>
            <h4>Seguro que hay uno muy cerca de ti</h4>

            <label>Nombre</label>
            <input type='text' value={inputName} onChange={handleNameChange} />
            <label>Ubicaci√≥n</label>
            <input type='text' value={inputLocation} onChange={handleLocationChange} />

            <button>BUSCAR</button>

            <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-evenly' }}>

                {filterCats.map((cat, index) => {
                    return (
                        <CardCat
                            key={index}
                            name={cat.name}
                            id={cat.id}
                            location={cat.location}
                            description={cat.description}
                            img={cat.img}
                        />
                    );
                })}
            </div>

        </div>
    )
}

export default InputCats
