import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import { useEffect, useState } from 'react';
import PlacesAutocomplete, { geocodeByAddress, getLatLng } from 'react-places-autocomplete';

const libraries = ['places'];

const defaultCenter = {
    lat: 40.4131401, lng: -3.706334
}

const GoogleMaps = () => {

    const [address, setAddress] = useState("");
    const [center, setCenter] = useState(defaultCenter);
    const [markerPosition, setMarkerPosition] = useState(null);

    const mapStyles = {
        height: "100vh",
        width: "80%"
    };

    const handleSelect = async (value) => {
        const results = await geocodeByAddress(value);
        const latLng = await getLatLng(results[0]);
        console.log(latLng);
        setAddress(value);
        setCenter(latLng);
        setMarkerPosition(latLng);
    };



    return (
        <div style={{ margin: '120px 0', display: 'flex', flexDirection }}>
            <h3>Encuentra centros de adopción cercanos</h3>

            <LoadScript googleMapsApiKey='AIzaSyDiT0oMbPlcoyQWL8eCXMiE71TmL1QYdOE' libraries={libraries}>
                <PlacesAutocomplete
                    value={address}
                    onChange={setAddress}
                    onSelect={handleSelect}
                >
                    {({ getInputProps, suggestions, getSuggestionItemProps, loading }) => (
                        <div>
                            <input {...getInputProps({ placeholder: "Buscar ubicación" })} />
                            <div>
                                {loading ? <div>Cargando...</div> : null}

                                {suggestions.map((suggestion) => {
                                    const style = {
                                        backgroundColor: suggestion.active ? "#41b6e6" : "#fff"
                                    };
                                    return (
                                        <div key={suggestion.placeId} {...getSuggestionItemProps(suggestion, { style })}>
                                            {suggestion.description}
                                        </div>
                                    );
                                })}
                            </div>
                        </div>
                    )}
                </PlacesAutocomplete>


                <GoogleMap
                    mapContainerStyle={mapStyles}
                    zoom={13}
                    center={center}>
                    {markerPosition !== null && <Marker position={markerPosition} />}
                </GoogleMap>


            </LoadScript>
        </div>
    )
}

export default GoogleMaps
