import { useContext, useState } from "react"
import { CatsApi } from "../../context/CatsApi"
import './Carrousel.css'


const Carrousel = () => {

    const { cats } = useContext(CatsApi)
    const numSlides = cats.length;
    const [flag, setFlag] = useState(0);
    const centerSlide = (flag % numSlides) + 1;
    const middleOfSlider = numSlides / 2;
    const xCoef = 300 * ((middleOfSlider - centerSlide) / middleOfSlider);

    function handleSlideClick(index) {
        setFlag(index - 1);
      }

      function handleIndicatorClick(index) {
        setFlag(index - 1);
      }

      function handleNextClick() {
        setFlag((flag + 1) % numSlides);
      }

      function handlePrevClick() {
        setFlag((Math.abs(flag - 1 + numSlides)) % numSlides);
      }


    return (
        <div id="slider-container">

            <div className="cats-carousel">
                {cats.map((cat) => {
                    return (
                        <div id="main-slider">
                            <div id={cat.id} className="slide card active">
                                <img className="card-img-top" alt={cat.name} src={cat.img} />
                                <div className="card-body">
                                    <h5 id="title-lavado" className="card-title">
                                        {cat.name}
                                    </h5>
                                    <p className="card-text">
                                        {cat.description}
                                    </p>
                                </div>
                            </div>


                        </div>
                    )

                })}
            </div>

            <div class="slide-indicator-container"></div>
            <div class="slider-controls">
                <div id="pre" class="slider-btn">
                    <i class="fa fa-chevron-left" aria-hidden="true"></i>
                </div>
                <div id="nex" class="slider-btn">
                    <i class="fa fa-chevron-right" aria-hidden="true"></i>
                </div>
            </div>



        </div >
    )
}

export default Carrousel
