import React, { useContext, useEffect, useState } from "react"
import { CatsApi } from "../../context/CatsApi"
import CardCat from "../CardCat/CardCat";

import './Carrousel.css'



const Carrousel = () => {

    const { cats } = useContext(CatsApi);
    const sliceCats = cats.slice(0, 7)
    const [position, setPosition] = useState(0);


    useEffect(() => {
        const interval = setInterval(() => {
            setPosition((position + 1) % sliceCats.length);
        }, 3000);

        return () => clearInterval(interval);
    }, [position, sliceCats]);


    return (
        <div id="slider-container" >
            <div className="slider-wrapper" style={{
                transform: `translateX(-${position * (100 / clonedCats.length)}%)`,
            }}>
                {clonedCats.map((cat, index) => {
                    const adjustedIndex = index % sliceCats.length;
                    return (
                        <CardCat
                            key={cat.id}
                            name={cat.name}
                            id={cat.id}
                            description={cat.description}
                            img={cat.img}
                            style={{
                                transform: `translateX(${adjustedIndex * 100}%)`
                            }}
                        />
                    )
                })}
            </div>
        </div>
    );
};

export default Carrousel;
