import { useContext, useState } from "react";
import { CatsApi } from "../../context/CatsApi";
import CardCat from "../CardCat/CardCat";


const InputCats = () => {

    const { cats } = useContext(CatsApi);
    const [search, setSearch] = useState('');
    const [locations, setLocation] = useState('');

    const handleChange = (event) => {
        setSearch(event.target.value);
    };

    const catsFiltered = cats.filter((cat) => {

        if (cat.name.toUpperCase().includes(search.toUpperCase()) || cat.name.toUpperCase().includes(search.toUpperCase())) {
            return true;
        }
        return false;

    });

    const handleChangeLocation = (event) => {
        setLocation(event.target.value);
    };





    return (
        <div>
            <h2>ENCUENTRA TU MICHI</h2>
            <h4>Seguro que hay uno muy cerca de ti</h4>

            <label>Nombre</label>
            <input type='text' value={search} onChange={handleChange} />
            <label>Ubicaci√≥n</label>
            <input type='text' value={locations} onChange={handleChangeLocation}/>

            <button>BUSCAR</button>

            <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-evenly' }}>

                {catsFiltered.map((cat, index) => {
                    return (
                        <CardCat
                            key={index}
                            name={cat.name}
                            id={cat.id}
                            location={cat.location}
                            description={cat.description}
                            img={cat.img}
                        />
                    )
                })}
            </div>

        </div>
    )
}

export default InputCats
