import { GoogleMap, LoadScript } from '@react-google-maps/api';
import { useState } from 'react';
import PlacesAutocomplete, { geocodeByAddress, getLatLng } from 'react-places-autocomplete';



const GoogleMaps = () => {

    const mapStyles = {
        height: "100vh",
        width: "100%"
    };

    const defaultCenter = {
        lat: 40.4165000, lng: -3.7025600
    }

    const [address, setAddress] = useState("");

    const handleSelect = async (value) => {
        const results = await geocodeByAddress(value);
        const latLng = await getLatLng(results[0]);
        console.log(latLng);
        setAddress(value);
    };

    // function addMarkers(map) {
    //     const locations = {
    //         Gatoteca: { lat: 40.4131401, lng: -3.9864854 },
    //         SosFelinos: { lat: 40.4131401, lng: -3.9864854 },
    //         WallaMascotas: { lat: 35.67445, lng: -7.0944514 },
    //         Spap: { lat: 40.4640858, lng: -3.9973309 },
    //         laMadrileña: { lat: 40.4640858, lng: -3.9973309 }
    //     }
    //     for (const location in locations) {
    //         const marker = new window.google.maps.Marker({
    //             position: locations[location],
    //             map: map,
    //             title: location,
    //             icon: './img/custom_pin.png'
    //         })
    //     }
    // }


    return (
        <div style={{ margin: '120px 0' }}>
            <h3>Encuentra centros de adopción cercanos</h3>

            <LoadScript googleMapsApiKey='AIzaSyDiT0oMbPlcoyQWL8eCXMiE71TmL1QYdOE'>
                <PlacesAutocomplete
                    value={address}
                    onChange={setAddress}
                    onSelect={handleSelect}
                >
                              {({ getInputProps, suggestions, getSuggestionItemProps, loading }) => (
            <div>
              <input {...getInputProps({ placeholder: "Buscar ubicación" })} />
              <div>
                {loading ? <div>Cargando...</div> : null}

                {suggestions.map((suggestion) => {
                  const style = {
                    backgroundColor: suggestion.active ? "#41b6e6" : "#fff"
                  };
                  return (
                    <div {...getSuggestionItemProps(suggestion, { style })}>
                      {suggestion.description}
                    </div>
                  );
                })}
              </div>
            </div>
          )}
        </PlacesAutocomplete>


                    <GoogleMap
                        mapContainerStyle={mapStyles}
                        zoom={13}
                        center={defaultCenter}
                    // onLoad={addMarkers}
                    />


            </LoadScript>
        </div>
    )
}

export default GoogleMaps
