import axios from 'axios'
import { useEffect, useState } from 'react'
import './Carrousel.css'


const apiURL = 'https://cats-adlb1ipvq-paulatealonso.vercel.app'

const Carrousel = () => {

    const [cats, setCats] = useState([])
    const [fetching, setFetching] = useState(true)

    useEffect(() => {
        axios
            .get(apiURL)
            .then((response) => {
                const catsNoKaren = response.data.cats.sort((a, b) => {
                    return (a.price - b.price)
                })

                console.log(catsNoKaren)

                setCats(catsNoKaren)
                setFetching(false)

            })
            .catch(err => console.error(err))
    }, [])



    return (
        <div className='carousel-cats'>
            {fetching && <h1>LOADING...</h1>}

            {cats.map((cat, id) => {
                return (
                    <div className="faders-cont">
                        <div className="faders">
                            <div className="left"></div>
                            <div className="right"></div>
                        </div>

                        <div className="items-carousel">
                            <div className="entry">
                                <p className="name-carousel">{cat.name}</p>
                                <img src={cat.img} alt={cat.name} />
                                <p className="quote">{cat.description}</p>
                            </div>
                        </div>
                    </div>
                )
            })}

        </div>
    )
}

export default Carrousel
