import { useContext } from "react";
import { CatsApi } from "../../context/CatsApi";
import CardCat from "../CardCat/CardCat";


const InputCats = () => {

    const { cats } = useContext(CatsApi);

  const [nameInput, setNameInput] = useState("");
  const [locationInput, setLocationInput] = useState("");

  const handleChangeName = (event) => {
    setNameInput(event.target.value);
  };

  const handleChangeLocation = (event) => {
    setLocationInput(event.target.value);
  };

  const filteredCats = cats
    ? cats
        .filter((cat) =>
          cat.name.toLowerCase().startsWith(nameInput.toLowerCase())
        )
        .filter((cat) =>
          cat.location.toLowerCase().startsWith(locationInput.toLowerCase())
        )
    : [];




    return (
        <div>
            <h2>ENCUENTRA TU MICHI</h2>
            <h4>Seguro que hay uno muy cerca de ti</h4>

            <label>Nombre</label>
            <input type='text' />
            <label>Ubicaci√≥n</label>
            <input type='text' />

            <button>BUSCAR</button>

            <div style={{display: 'flex', flexWrap: 'wrap', justifyContent: 'space-evenly'}}>

                {cats.map((cat, index) => {
                    return (
                        <CardCat
                            key={index}
                            name={cat.name}
                            id={cat.id}
                            location={cat.location}
                            description={cat.description}
                            img={cat.img}
                        />
                    )
                })}
            </div>

        </div>
    )
}

export default InputCats
