import React, { useContext, useEffect, useState } from "react"
import { CatsApi } from "../../context/CatsApi"
import CardCat from "../CardCat/CardCat";

import './Carrousel.css'



const Carrousel = () => {

    const { cats } = useContext(CatsApi);
    const sliceCats = cats.slice(0, 7)

    const [position, setPosition] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
          setPosition((position + 1) % (sliceCats.length + 1));
        }, 3000);

        return () => clearInterval(interval);
      }, [position, sliceCats]);

      const clonedCats = [...sliceCats, ...sliceCats.slice(0, 2)];


    return (
        <div id="slider-container" >
            <div
                className="slider-wrapper"
                style={{ transform: `translateX(-${position * 100}%)` }}
            >
                {sliceCats.map((cat) => {
                    return (
                        <CardCat
                            name={cat.name}
                            id={cat.id}
                            description={cat.description}
                            img={cat.img}
                        />
                    )
                })}
            </div>
        </div>
    );
};

export default Carrousel;
