import React, { useContext, useEffect, useState} from "react"
import { CatsApi } from "../../context/CatsApi"
import './Carrousel.css'


const Carrousel = () => {

    const { cats } = useContext(CatsApi)
    const [activeIndex, setActiveIndex] = useState(0);

    useEffect(() => {
        const slideNum = cats.length;
        const addListeners = [".slide-indicator-container", "#main-slider"];


        const indicators = [];
        for (let i = 0; i < slideNum; i++) {
          indicators.push(
            <div
              key={`indicator-${i}`}
              id={`indicator-${i}`}
              className={`slide-indicator indicator-${i}`}
              onClick={() => setActiveIndex(i)}
            ></div>
          );
        }

        // first slide active by default
        indicators[0] = React.cloneElement(indicators[0], { className: "slide-indicator indicator-0 active" });

        // Add functionality to next and previous buttons
        const nextSlide = () => setActiveIndex((activeIndex + 1) % slideNum);
        const prevSlide = () => setActiveIndex((activeIndex - 1 + slideNum) % slideNum);

        // Interval for automatic sliding
        const interval = setInterval(() => nextSlide(), 5000);

        // Cleanup interval on unmount
        return () => clearInterval(interval);

      }, [cats]);



    return (
        <div id="slider-container">

            <div className="cats-carousel">
                {cats.map((cat) => {
                    return (
                        <div id="main-slider">
                            <div id={cat.id} className="slide card active">
                                <img className="card-img-top" alt={cat.name} src={cat.img} />
                                <div className="card-body">
                                    <h5 id="title-lavado" className="card-title">
                                        {cat.name}
                                    </h5>
                                    <p className="card-text">
                                        {cat.description}
                                    </p>
                                </div>
                            </div>


                        </div>
                    )

                })}
            </div>

            <div class="slide-indicator-container"></div>
            <div class="slider-controls">
                <div id="pre" class="slider-btn">
                    <i class="fa fa-chevron-left" aria-hidden="true"></i>
                </div>
                <div id="nex" class="slider-btn">
                    <i class="fa fa-chevron-right" aria-hidden="true"></i>
                </div>
            </div>



        </div >
    )
}

export default Carrousel
