import React, { useContext, useEffect, useState } from "react"
import { CatsApi } from "../../context/CatsApi"
import './Carrousel.css'


const Carrousel = () => {

    const { cats } = useContext(CatsApi);
    const [activeIndex, setActiveIndex] = useState(0);

    useEffect(() => {
        const slideNum = cats.length;
        const addListeners = [".slide-indicator-container", "#main-slider"];

        const indicators = [];
        for (let i = 0; i < slideNum; i++) {
            indicators.push(
                <div
                    key={`indicator-${i}`}
                    id={`indicator-${i}`}
                    className={`slide-indicator indicator-${i}`}
                    onClick={() => setActiveIndex(i)}
                ></div>
            );
        }

        if (indicators.length > 0) {
            indicators[0] = React.cloneElement(indicators[0], { className: "slide-indicator indicator-0 active" });
        }

        const nextSlide = () => setActiveIndex((activeIndex + 1) % slideNum);
        const prevSlide = () => setActiveIndex((activeIndex - 1 + slideNum) % slideNum);

        const interval = setInterval(() => nextSlide(), 5000);

        return () => clearInterval(interval);

    }, [cats]);

    const indicators = cats.map((_, i) => (
        <div
          key={`indicator-${i}`}
          id={`indicator-${i}`}
          className={`slide-indicator indicator-${i}`}
          onClick={() => setActiveIndex(i)}
        ></div>
      ))

      if (indicators.length === 0) {
        return null // o un mensaje de carga, etc.
      }

      indicators[0] = React.cloneElement(indicators[0], { className: "slide-indicator indicator-0 active" })




    return (
        <div id="slider-container">
            <div className="cats-carousel">
                {cats.map((cat, i) => {
                    const isActive = activeIndex === i;
                    const translateX = (i - activeIndex) * 100;

                    return (
                        <div
                            key={`slide-${i}`}
                            id={`slide-${i}`}
                            className={`slide card ${isActive ? "active" : ""}`}
                            style={{ transform: `translateX(${translateX}%) scale(${isActive ? 1.5 : 1})` }}
                        >
                            <img className="card-img-top" alt={cat.name} src={cat.img} />
                            <div className="card-body">
                                <h5 id="title-lavado" className="card-title">
                                    {cat.name}
                                </h5>
                                <p className="card-text">{cat.description}</p>
                            </div>
                        </div>
                    );
                })}
            </div>

            <div className="slide-indicator-container">
            {indicators}
            </div>
            <div className="slider-controls">
                <div id="pre" className="slider-btn" onClick={() => setActiveIndex((activeIndex - 1 + cats.length) % cats.length)}>
                    <i className="fa fa-chevron-left" aria-hidden="true"></i>
                </div>
                <div id="nex" className="slider-btn" onClick={() => setActiveIndex((activeIndex + 1) % cats.length)}>
                    <i className="fa fa-chevron-right" aria-hidden="true"></i>
                </div>
            </div>
        </div>
    );
};

export default Carrousel;
