import { useState } from "react";
import './PaymentForm.css'

const PaymentForm = ({ title }) => {
    const [selectedMethod, setSelectedMethod] = useState(null);
    const [showDebitForm, setShowDebitForm] = useState(false);

    const handleRadioChange = event => {
        setSelectedMethod(event.target.value);
        if (event.target.value === 'debit') {
            setShowDebitForm(true);
        } else {
            setShowDebitForm(false);
        }
    };

    const [formData, setFormData] = useState({
        name: '',
        debt: '',
        date: '',
        cvc: '',
    });


    const [errors, setErrors] = useState({
        name: '',
        debt: '',
        date: '',
        cvc: '',
    });

    const handleChange = (event) => {
        setFormData({
            ...formData,
            [event.target.name]: event.target.value,
        });

        setErrors({
            ...errors,
            [event.target.name]: '',
        });
    };
    const validate = () => {
        let nameError = '';
        let debtError = '';
        let dateError = '';
        let cvcError = '';

        if (!formData.name) {
            nameError = 'Name is required';
        }

        if (formData.name.length < 2) {
            nameError = 'Name is required';
        }

        if (!formData.debt) {
            debtError = 'Tarjet number is required';
        } else if (/\S+@\S+\.\S+/.test(formData.debt)) {
            debtError = 'Tarjet number is not valid';
        } else if (formData.debt.length < 15) {
            debtError = 'Tarjet number is not valid';
        }

        if (!formData.date) {
            dateError = 'Date is required';
        }

        if (!formData.cvc) {
            cvcError = 'CVC is required';
        }

        if (formData.cvc < 3) {
            cvcError = 'CVC is required';
        }

        if (nameError || debtError || cvcError || dateError) {
            setErrors({ name: nameError, debt: debtError, date: dateError, cvc: cvcError, });
            return false;
        }

        return true;
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        const isValid = validate();
        if (isValid) {
            console.log(formData);
            setFormData({ name: '', debt: '', date: '', cvc: '' });
        }
    };

    // SECOND FORM


    const [formDataTwo, setFormDataTwo] = useState({
        name: '',
        lastName: '',
        phone: '',
        email: '',
        country: '',
        poblation: '',
        postal: '',
        street: '',
        num: '',
        piso: '',
        door: '',
    });

    const [errorsTwo, setErrorsTwo] = useState({});

    const handleInputChange = (event) => {
        setFormDataTwo({
            ...formData,
            [event.target.name]: event.target.value,
        });
    };

    const handleSubmitTwo = (event) => {
        event.preventDefault();
        setErrors({});

        const newErrors = {};

        if (!formData.name) {
            newErrors.name = 'El nombre es obligatorio';
        }

        if (formData.name.length < 2) {
            newErrors.name = 'El nombre no es válido';
        }

        if (!formData.lastName) {
            newErrors.lastName = 'El apellido es obligatorio';
        }

        if (!formData.phone) {
            newErrors.phone = 'El teléfono es obligatorio';
        }

        if (formData.phone.length < 7) {
            newErrors.phone = 'El teléfono es obligatorio';
        }

        if (!formData.email) {
            newErrors.email = 'El correo electrónico es obligatorio';
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = 'El correo electrónico no es válido';
        }

        if (!formData.country) {
            newErrors.country = 'El país es obligatorio';
        }
        if (!formData.poblation) {
            newErrors.poblation = 'La ciudad es obligatoria';
        }
        if (!formData.postal) {
            newErrors.postal = 'El código postal es necesario';
        }

        if (formData.postal.length < 4) {
            newErrors.postal = 'El código postal no es válido';
        }
        if (!formData.street) {
            newErrors.street = 'La calle es necesaria';
        }
        if (!formData.num) {
            newErrors.num = 'El número de calle es necesario';
        }

        if (Object.keys(newErrors).length > 0) {
            setErrors(newErrors);
            return;
        }

    };




    return (
        <div>
            <div id='conteiner-paid'>
                <h3>{title}</h3>
                <h5>Seleccionar método de pago</h5>
                <div id='conteiner-debit'>
                    <input
                        type="radio"
                        id="debit"
                        name="paymentMethod"
                        value="debit"
                        checked={selectedMethod === "debit"}
                        onChange={handleRadioChange}
                    />
                    <label className='label-bold'>Tarjeta de débito <br /> <span className='span-separ'>Opción estándar sin seguimiento </span></label>
                </div>
                {showDebitForm && (
                    <form onChange={handleSubmit}>
                        <div id='titular-form'>
                            <label>Titular</label>
                            <br />
                            <input
                                type="text"
                                name="name"
                                value={formData.name}
                                placeholder='Nombre del titular'
                                onChange={handleChange}
                                id='titular'
                            />
                            {errors.name && <div style={{ color: '#ffbadb' }}>{errors.name}</div>}
                        </div>

                        <div id='target-form'>
                            <label>Número de tarjeta</label>
                            <br />
                            <input
                                type="text"
                                name="debt"
                                placeholder="1234 1234 1234 1234"
                                value={formData.debt}
                                onChange={handleChange}
                                id='debt'
                            />
                            {errors.debt && <div style={{ color: '#ffbadb' }}>{errors.debt}</div>}
                        </div>
                        <div id='expiration-cvc'>
                            <div id='expiration-form'>
                                <label>Fecha de caducidad</label>
                                <br />
                                <input
                                    type="text"
                                    name="date"
                                    placeholder="MM / YY"
                                    value={formData.date}
                                    onChange={handleChange}
                                    id='tel'
                                />
                                {errors.date && <div style={{ color: '#ffbadb' }}>{errors.date}</div>}
                            </div>

                            <div id='secret-form'>
                                <label>CVC</label>
                                <br />
                                <input
                                    type="text"
                                    name="cvc"
                                    placeholder="123"
                                    value={formData.cvc}
                                    onChange={handleChange}
                                    id='cvc'
                                />
                                {errors.cvc && <div style={{ color: '#ffbadb' }}>{errors.cvc}</div>}
                                <br />
                            </div>
                        </div>
                    </form>
                )}

                <div id='conteiner-transferencia'>
                    <input
                        type="radio"
                        id="trans"
                        name="paymentMethod"
                        value="trans"
                        checked={selectedMethod === "trans"}
                        onChange={handleRadioChange}
                    />
                    <label className='label-bold'>Transferencia bancaria a la cuenta ES12 1234 1234 123457890 <br /><span className='span-separ'> Será necesario recibir el comprobante de la transferencia para preparar tu pedido </span></label>
                </div>
                <div id='conteiner-bizum'>
                    <input
                        type="radio"
                        id="bizum"
                        name="paymentMethod"
                        value="bizum"
                        checked={selectedMethod === "bizum"}
                        onChange={handleRadioChange}
                    />
                    <label className='label-bold'>Bizum</label>
                    {/* <img src={bizum} alt='bizum' id='img-bizum'/> */}
                </div>
            </div>


            {/* SEGUNDO FORM */}

            <form onChange={handleSubmit}>
                <div id="formName">
                    <label htmlFor="name">Nombre:</label>
                    <input
                        type="text"
                        id="name"
                        name="name"
                        value={formDataTwo.name}
                        onChange={handleInputChange}
                    />
                    {errors.name && <div style={{ color: 'red' }}>{errors.name}</div>}
                </div>

                <div id="formLastName">
                    <label htmlFor="lastName">Apellidos:</label>
                    <input
                        type="text"
                        id="lastName"
                        name="lastName"
                        value={formData.lastName}
                        onChange={handleInputChange}
                    />
                    {errors.lastName && <div style={{ color: 'red' }}>{errors.lastName}</div>}
                </div>

                <div id="formPhone">
                    <label htmlFor="phone">Teléfono:</label>
                    <input
                        type="text"
                        id="phone"
                        name="phone"
                        value={formData.phone}
                        onChange={handleInputChange}
                    />
                    {errors.phone && <div style={{ color: 'red' }}>{errors.phone}</div>}
                </div>

                <div id="formEmail">
                    <label htmlFor="email">Email:</label>
                    <input
                        type="text"
                        id="email"
                        name="email"
                        value={formData.email}
                        onChange={handleInputChange}
                    />
                    {errors.email && <div style={{ color: 'red' }}>{errors.email}</div>}
                </div>

                <div id="formCountry">
                    <label htmlFor="country">País</label>
                    <input
                        type="text"
                        id="country"
                        name="country"
                        value={formData.country}
                        onChange={handleInputChange}
                    />
                    {errors.country && <div style={{ color: 'red' }}>{errors.country}</div>}
                </div>

                <div id="box-city">
                    <div id="formPoblation">
                        <label htmlFor="poblation">Población</label>
                        <input
                            type="text"
                            id="poblation"
                            name="poblation"
                            value={formData.poblation}
                            onChange={handleInputChange}
                        />
                        {errors.poblation && <div style={{ color: 'red' }}>{errors.poblation}</div>}
                    </div>

                    <div id="formPostal">
                        <label htmlFor="postal">Código postal</label>
                        <input
                            type="text"
                            id="postal"
                            name="postal"
                            value={formData.postal}
                            onChange={handleInputChange}
                        />
                        {errors.postal && <div style={{ color: 'red' }}>{errors.postal}</div>}
                    </div>

                </div>

                <div id="box-street">

                    <div id="formStreet">
                        <label htmlFor="street">Calle</label>
                        <input
                            type="text"
                            id="street"
                            name="street"
                            value={formData.street}
                            onChange={handleInputChange}
                        />
                        {errors.street && <div style={{ color: 'red' }}>{errors.street}</div>}
                    </div>

                    <div id="formNum">
                        <label htmlFor="num">Número</label>
                        <input
                            type="text"
                            id="num"
                            name="num"
                            value={formData.num}
                            onChange={handleInputChange}
                        />
                        {errors.num && <div style={{ color: 'red' }}>{errors.num}</div>}
                    </div>

                    <div id="formPiso">
                        <label htmlFor="piso">Piso</label>
                        <input
                            type="text"
                            id="piso"
                            name="piso"
                            value={formData.piso}
                            onChange={handleInputChange}
                        />
                    </div>

                    <div id="formDoor">
                        <label htmlFor="door">Puerta</label>
                        <input
                            type="text"
                            id="door"
                            name="door"
                            value={formData.door}
                            onChange={handleInputChange}
                        />
                    </div>
                </div>

            </form>


        </div>
    );
};
export default PaymentForm
