import React, { useContext, useState } from "react"
import { CatsApi } from "../../context/CatsApi"
import CardCat from "../CardCat/CardCat";

import './Carrousel.css'



const Carrousel = () => {

    const { cats } = useContext(CatsApi);
    const sliceCats = cats.slice(0, 7)
    const [position, setPosition] = useState(0);


    const clonedCats = [...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats, ...sliceCats];

    const handlePrev = () => {
        setPosition((position - 1 + sliceCats.length) % sliceCats.length);
    }

    const handleNext = () => {
        setPosition((position + 1) % sliceCats.length);
    }

    return (
        <div id="slider-container" >
            <button onClick={handlePrev}>Anterior</button>
            <div className="slider-wrapper" >
                {clonedCats.map((cat) => {
                    return (
                        <CardCat
                            key={cat.id}
                            name={cat.name}
                            id={cat.id}
                            description={cat.description}
                            img={cat.img}
                        />
                    )
                })}
            </div>
        </div>
    );
};

export default Carrousel;
